mapper.select:
select count(id)
from dashboard
where name = #{name}
  and organization_id = #{orgId}
  and dashboard_module_id = #{dashboardModuleId}

      and id != #{id}

SELECT max(pos)
FROM dashboard
where organization_id = #{orgId}

SELECT id, NAME, dashboard_module_id as parent_id, organization_id, 'DASHBOARD' as type, resource_id FROM dashboard WHERE organization_id = #{orgId}
AND (( JSON_CONTAINS(`scope_id`, json_array(#{userId})) or `scope_id` = '[]'
or JSON_CONTAINS(`scope_id`, json_array(#{departmentId}))
) or create_user = #{userId} )

select d.id, d.name, d.resource_id, d.dashboard_module_id, d.scope_id, d.description, d.create_time, d.update_time, d.pos, d.organization_id, d.create_user, d.update_user, dm.NAME as dashboardModuleName from dashboard d INNER JOIN dashboard_module dm ON d.dashboard_module_id = dm.id where d.organization_id = #{orgId}
AND ( ( JSON_CONTAINS(d.`scope_id`, json_array(#{userId})) or d.`scope_id` = '[]'
or JSON_CONTAINS(d.`scope_id`, json_array(#{departmentId}))
) or d.create_user = #{userId} )
and d.name like concat('%', #{request.keyword},'%')
and d.dashboard_module_id in
#{dashboardModuleId}

choose.when:

order by


        d.`create_time` ${sortType}


        d.`name` ${sortType}


        d.`dashboard_module_id` ${sortType}


        d.`create_user` ${sortType}


        order by d.create_time desc

mapper.select:
SELECT id, pos
FROM dashboard
WHERE id = #{dragNodeId}

SELECT id, pos
FROM dashboard
WHERE organization_id = #{nodeSortQueryParam.parentId}

    AND pos > #{nodeSortQueryParam.pos}


    AND pos < #{nodeSortQueryParam.pos}

ORDER BY pos

    DESC

LIMIT 1

mapper.update:
update dashboard
set pos =#{pos}
where id = #{id}
