mapper.select:
select count(id) from dashboard_module
where name = #{name}
  and organization_id = #{orgId}
  and parent_id = #{parentId}

    and id != #{id}

SELECT max(pos)
FROM dashboard_module
where organization_id = #{orgId}

SELECT count(id) FROM dashboard_module WHERE id IN
#{id}
AND parent_id = 'NONE' and organization_id = #{orgId}

select count(id)
from dashboard
where dashboard_module_id in

    #{dashboardModuleId}

and organization_id = #{orgId}

mapper.update:
UPDATE dashboard_module
SET pos = CASE

    WHEN id = #{dashboardModule.id} THEN #{dashboardModule.pos}

END
WHERE id IN

    #{dashboardModule.id}

mapper.select:
SELECT id, name, pos, organization_id, parent_id
FROM dashboard_module
WHERE id = #{dragNodeId}

SELECT id, name, pos, organization_id, parent_id
FROM dashboard_module
WHERE parent_id = #{nodeSortQueryParam.parentId}

    AND pos > #{nodeSortQueryParam.pos}


    AND pos < #{nodeSortQueryParam.pos}

ORDER BY pos

    DESC

LIMIT 1
