mapper.select:
select count(id)
from agent
where name = #{name}
and organization_id = #{orgId}
and agent_module_id = #{agentModuleId}

    and id != #{id}

SELECT max(pos)
FROM agent
where organization_id = #{orgId}

select a.id, a.name, a.agent_module_id, a.scope_id, a.script, a.description, a.create_time, a.update_time, a.pos, a.organization_id, a.create_user, a.update_user, am.NAME as agentModuleName from agent a INNER JOIN agent_module am ON a.agent_module_id = am.id where a.organization_id = #{orgId}
AND ( ( JSON_CONTAINS(a.`scope_id`, json_array(#{userId})) or a.`scope_id` = '[]'
or JSON_CONTAINS(a.`scope_id`, json_array(#{departmentId}))
) or a.create_user = #{userId} )
and a.name like concat('%', #{request.keyword},'%')
and a.agent_module_id in
#{agentModuleId}

choose.when:

order by


        a.`create_time` ${sortType}


        a.`name` ${sortType}


        a.`agent_module_id` ${sortType}


        a.`create_user` ${sortType}


        order by a.create_time desc
