mapper.select:
select count(id) from agent_module
where name = #{name}
and organization_id = #{orgId}
and parent_id = #{parentId}

    and id != #{id}

SELECT max(pos)
FROM agent_module
where organization_id = #{orgId}

select count(id)
from agent
where agent_module_id in

    #{agentModuleId}

and organization_id = #{orgId}

SELECT id, name, pos, organization_id, parent_id
FROM agent_module
WHERE id = #{dragNodeId}

SELECT id, name, pos, organization_id, parent_id
FROM agent_module
WHERE parent_id = #{nodeSortQueryParam.parentId}

    AND pos > #{nodeSortQueryParam.pos}


    AND pos < #{nodeSortQueryParam.pos}

ORDER BY pos

    DESC

LIMIT 1

mapper.update:
update agent_module
set pos =#{pos}
where id = #{id}
