mapper.select:
SELECT max(pos)
FROM sys_user_view
where organization_id = #{orgId}
and user_id = #{userId}
and resource_type = #{resourceType}

select count(id) from sys_user_view
where organization_id = #{orgId}
and user_id = #{userId}
and id = #{id}

select id, name, fixed, enable from sys_user_view
where organization_id = #{orgId}
and user_id = #{userId}
and resource_type = #{resourceType}
order by pos desc

