mapper.select:
SELECT
id,
customer_id,
opportunity_id,
type,
clue_id,
content,
owner,
contact_id,
estimated_time,
create_time,
update_time,
create_user,
update_user,
method,
converted,
status
FROM
follow_up_plan
WHERE
organization_id =#{orgId}

    AND customer_id = #{request.sourceId}


    AND clue_id = #{request.sourceId}
    AND create_user = #{userId}


    AND opportunity_id = #{request.sourceId}


    AND type = #{type}


    AND organization_id = #{orgId}


    AND content like concat('%', #{request.keyword},'%')


    AND owner = #{userId}


    AND status = #{request.status}




        AND owner not in

            #{userId}






        AND owner not in

            #{userId}





    AND owner = #{userId}



    AND type in



                'CUSTOMER'


                #{type}



and


            customer_id is not null


            opportunity_id is not null


            clue_id is not null





ORDER BY estimated_time DESC, create_time DESC

select count(*) from follow_up_plan c


    join sys_organization_user sou on c.owner = sou.user_id
    and (
    sou.department_id in

        #{deptId}


        or sou.user_id = #{request.userId}

    )


where c.organization_id = #{request.orgId}

    and c.create_time >= #{request.startTime} and c.create_time <= #{request.endTime}



    and c.owner = #{request.userId}
