mapper.select:
SELECT
    id,
    customer_id,
    opportunity_id,
    type,
    lead_id,
    content,
    owner,
    contact_id,
    create_time,
    update_time,
    create_user,
    update_user
FROM
    follow_up_record
WHERE
    organization_id =#{orgId}
  and type = #{type}

    AND customer_id = #{request.sourceId}


    AND lead_id = #{request.sourceId}
    AND create_user = #{userId}


    AND opportunity_id = #{request.sourceId}


    AND type = #{type}


    AND organization_id = #{orgId}


    AND content like concat('%', #{request.keyword},'%')

ORDER BY create_time DESC
