mapper.select:
select * from lead_pool
where organization_id = #{orgId}

order by create_time desc

mapper.update:
update lead_pool_pick_rule set limit_on_number = #{rule.limitOnNumber}, pick_number = #{rule.pickNumber}, limit_pre_owner = #{rule.limitPreOwner},
                                   pick_interval_days = #{rule.pickIntervalDays}, update_user = #{rule.updateUser}, update_time = #{rule.updateTime}
where pool_id = #{rule.poolId}

update lead_pool_recycle_rule set expire_notice = #{rule.expireNotice}, notice_days = #{rule.noticeDays},
                                      operator = #{rule.operator}, `condition` = #{rule.condition}, update_user = #{rule.updateUser}, update_time = #{rule.updateTime}
where pool_id = #{rule.poolId}
