mapper.update:
UPDATE sys_announcement SET notice = #{notice} WHERE organization_id = #{organizationId}

    and id in

        #{id}

mapper.select:
SELECT *
FROM sys_announcement
WHERE end_time  >=  #{timestamp}
  and start_time  <  #{timestamp}
  and notice = false

SELECT id
FROM sys_announcement
WHERE end_time  <  #{endTime}
and end_time  >  #{startTime}
