    and id in

        #{id}

mapper.select:
SELECT sys_announcement.*, sys_user.name as createUserName FROM sys_announcement LEFT JOIN sys_user ON sys_announcement.create_user = sys_user.id WHERE sys_announcement.organization_id = #{organizationId}
and sys_announcement.subject like concat('%', #{keyword},'%')
ORDER BY sys_announcement.create_time DESC;

SELECT *
FROM sys_announcement
WHERE end_time  >=  #{timestamp}
  and start_time  <  #{timestamp}
  and notice = false
