mapper.select:
SELECT max(num)
FROM sys_department
where organization_id = #{orgId}

SELECT id, name, num, organization_id, parent_id
FROM sys_department
WHERE id = #{dragNodeId}

SELECT id, name, num, organization_id, parent_id
FROM sys_department
WHERE parent_id = #{nodeSortQueryParam.parentId}

    AND num > #{nodeSortQueryParam.num}


    AND num < #{nodeSortQueryParam.num}

ORDER BY num

    DESC

LIMIT 1

mapper.update:
UPDATE sys_department
SET num = CASE

    WHEN id = #{department.id} THEN #{department.num}

END
WHERE id IN

    #{department.id}
