mapper.update:
update export_task
set status = #{newStatus}
where status = #{oldStatus}

mapper.select:
SELECT * FROM export_task WHERE create_user = #{userId} and create_time > #{oneDayBefore}
and file_name like concat('%', #{request.keyword},'%')
and resource_type like concat(#{request.exportType},'%')
and status = #{request.exportStatus}
ORDER BY create_time DESC
