mapper.select:
SELECT count(id) FROM sys_organization_config_detail WHERE config_id = #{configId}

    and name like concat('%', #{name},'%')


    and type = #{type}

SELECT * FROM sys_organization_config_detail WHERE config_id = #{configId}

    and name like concat('%', #{name},'%')

and type = #{type}

SELECT count(id)
FROM sys_organization_config_detail
WHERE sys_organization_config_detail.name = #{name}

    and id != #{id}
